dff=pd.DataFrame({'year':[],'stage':[],'team_name':[],'value_team':[]})
for a in df.index:
	stage=int(df.loc[a]['values'])
	year=int(df.loc[a]['year'])
	if a!=0 and year==df.loc[a-1]['year']:
		payload['_ctl0:MainContentPlaceHolderMaster:gruposDropDownList']=f'{stage}'
	else:
		payload['_ctl0:MainContentPlaceHolderMaster:temporadasDropDownList']=f'{year}'
	response= requests.post(url,data=payload)
	print(year,stage,response)
	soup=bs(response.text)
	options=soup.find('select',{'id':'_ctl0_MainContentPlaceHolderMaster_equiposDropDownList'}).find_all('option')
	value_teams=[]
	team_names=[]
	stages=[df.loc[a]['stages'].strip()]*len(options)
	value_stages=[f'{stage}']*len(options)
	years=[f'{year}']*len(options)

	for option in options:
		value=option.get('value')
		team_name=option.text.strip()
		value_teams.append(value)
		team_names.append(team_name)

	dfff=pd.DataFrame({'year':years,'stage':stages,'value_stage':value_stages,'team_name':team_names,'value_team':value_teams})

	dff=pd.concat([dff,dfff],axis=0).reset_index(drop=True)
	if a%5==0:
		print(dff)
	time.sleep(5)

dff.to_pickle('team_names_stages.pkl')

